2.1 Temperature

dft &lt;- 
  import("data/clima.csv", dec = ",") |&gt; 
  separate(hora, into = c("dia", "mes", "ano")) |&gt; 
  unite("data", dia, mes, ano, sep = "/") |&gt; 
  mutate(data = dmy(data))

dftemp &lt;- 
  dft |&gt; 
  group_by(data) |&gt; 
  summarise(tmin = min(tmin),
            tmed = mean(tmed),
            tmax = max(tmax),
            prec = sum(prec),
            ur = mean(ur)) |&gt; 
  mutate(gd = tmed - 5,
         gd2 = ((tmax + tmin) / 2) - 5) |&gt; 
  mutate(data = ymd(data))

dftempe1 &lt;- 
  dftemp |&gt; 
  slice(1:79) |&gt; 
  mutate(epoca = "E1")

dftempe2 &lt;- 
  dftemp |&gt; 
  slice(79:148) |&gt; 
  mutate(epoca = "E2")

dftemp2 &lt;- 
  bind_rows(dftempe1, dftempe2) |&gt; 
  relocate(epoca, .before = data) |&gt; 
  group_by(epoca) |&gt; 
  mutate(gda = cumsum(gd),
         gda2 = cumsum(gd2)) |&gt; 
  separate(data, into = c("ano", "mes", "dia")) |&gt; 
  unite("data", dia, mes, sep = "/")

